SELECT je.id, je.journal_date, je.description, je.status, je.version, je.created_by, je.approved_by, je.created_at, je.approved_at, je.rejected_by, je.rejected_at, je.rejection_reason, je.updated_at, je.voucher_number, je.input_date, je.is_closing_entry, je.is_single_entry, je.voucher_type, je.is_recurring, je.employee_code, je.department_code, je.is_red_slip, je.red_black_voucher_number, jel.id AS line_id, jel.journal_entry_id AS line_journal_entry_id, jel.line_number, jel.account_id, jel.debit_amount, jel.credit_amount, jel.line_description FROM journal_entries je LEFT JOIN journal_entry_lines jel ON je.id = jel.journal_entry_id
je.status IN
#{status}
AND je.journal_date >= #{dateFrom}
AND je.journal_date <= #{dateTo}
ORDER BY je.id ASC, jel.line_number ASC LIMIT #{limit} OFFSET #{offset}

