SELECT DISTINCT je.id, je.journal_date, je.description, je.status, je.version, je.created_by, je.approved_by, je.created_at, je.approved_at, je.rejected_by, je.rejected_at, je.rejection_reason, je.updated_at, je.voucher_number, je.input_date, je.is_closing_entry, je.is_single_entry, je.voucher_type, je.is_recurring, je.employee_code, je.department_code, je.is_red_slip, je.red_black_voucher_number, jel.id AS line_id, jel.journal_entry_id AS line_journal_entry_id, jel.line_number, jel.account_id, jel.debit_amount, jel.credit_amount, jel.line_description FROM journal_entries je LEFT JOIN journal_entry_lines jel ON je.id = jel.journal_entry_id
je.status IN
#{status}
AND je.journal_date >= #{c.dateFrom}
AND je.journal_date <= #{c.dateTo}
AND je.id IN ( SELECT DISTINCT jel2.journal_entry_id FROM journal_entry_lines jel2 WHERE jel2.account_id = #{c.accountId} )
AND je.id IN ( SELECT jel3.journal_entry_id FROM journal_entry_lines jel3 GROUP BY jel3.journal_entry_id HAVING SUM(jel3.debit_amount) >= #{c.amountFrom} )
AND je.id IN ( SELECT jel4.journal_entry_id FROM journal_entry_lines jel4 GROUP BY jel4.journal_entry_id HAVING SUM(jel4.debit_amount) <= #{c.amountTo} )
AND je.description LIKE CONCAT('%', #{c.description}, '%')
ORDER BY je.id ASC, jel.line_number ASC LIMIT #{c.limit} OFFSET #{c.offset}

SELECT COUNT(DISTINCT je.id)
FROM journal_entries je


        je.status IN

            #{status}



        AND je.journal_date >= #{c.dateFrom}


        AND je.journal_date <= #{c.dateTo}


        AND je.id IN (
            SELECT DISTINCT jel2.journal_entry_id
            FROM journal_entry_lines jel2
            WHERE jel2.account_id = #{c.accountId}
        )


        AND je.id IN (
            SELECT jel3.journal_entry_id
            FROM journal_entry_lines jel3
            GROUP BY jel3.journal_entry_id
            HAVING SUM(jel3.debit_amount) >= #{c.amountFrom}
        )


        AND je.id IN (
            SELECT jel4.journal_entry_id
            FROM journal_entry_lines jel4
            GROUP BY jel4.journal_entry_id
            HAVING SUM(jel4.debit_amount) <= #{c.amountTo}
        )


        AND je.description LIKE CONCAT('%', #{c.description}, '%')

SELECT je.id AS journal_entry_id, je.journal_date, je.description, jel.line_description, jel.debit_amount, jel.credit_amount FROM journal_entries je INNER JOIN journal_entry_lines jel ON je.id = jel.journal_entry_id
je.status = 'POSTED' AND jel.account_id = #{accountId}
AND je.journal_date >= #{dateFrom}
AND je.journal_date <= #{dateTo}
ORDER BY je.journal_date ASC, je.id ASC, jel.line_number ASC LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*)
FROM journal_entries je
INNER JOIN journal_entry_lines jel ON je.id = jel.journal_entry_id

    je.status = 'POSTED'
    AND jel.account_id = #{accountId}

        AND je.journal_date >= #{dateFrom}


        AND je.journal_date <= #{dateTo}

SELECT COALESCE(SUM(jel.debit_amount - jel.credit_amount), 0)
FROM journal_entries je
INNER JOIN journal_entry_lines jel ON je.id = jel.journal_entry_id
WHERE je.status = 'POSTED'
  AND jel.account_id = #{accountId}
  AND je.journal_date < #{date}

SELECT je.journal_date AS date, SUM(jel.debit_amount) AS debit_total, SUM(jel.credit_amount) AS credit_total, COUNT(DISTINCT je.id) AS transaction_count FROM journal_entries je INNER JOIN journal_entry_lines jel ON je.id = jel.journal_entry_id
je.status = 'POSTED' AND jel.account_id = #{accountId}
AND je.journal_date >= #{dateFrom}
AND je.journal_date <= #{dateTo}
GROUP BY je.journal_date ORDER BY je.journal_date ASC
